    const args = this.popupObj;
    this.parent.trigger(quickToolbarOpen, args);
  }
  /**
   * renderPopup method
   *
   * @param {BaseQuickToolbar} args - specifies  the arguments.
   * @returns {void}
   * @hidden
   * @deprecated
   */


  renderPopup(args) {
    this.setPanel(args.element);
    this.renderPanel();
